 <style>    #sortable1, #sortable2 { list-style-type: none; margin: 0; padding: 0 0 2.5em; float: left; margin-right: 10px; }    #sortable1 li, #sortable2 li { margin: 0 5px 5px 5px; padding: 5px; font-size: 1.2em; width: 120px; }    </style>    <script>    $(function() {        $( "#sortable1, #sortable2" ).sortable({            connectWith: ".connectedSortable",            receive:  function (event, ui) {               sendsortable()           },            update:  function (event, ui) {              sendsortable()           }                   }).disableSelection();                       });                function  sendsortable()   {    	var sortedIDs = $( "#sortable2" ).sortable( "serialize");    	console.log(sortedIDs)    }    </script>