<div id="langBox">	 <?php if($dObjLingue->nLang>1 || SEO_ENABLED==1):         foreach($dObjLingue->dataList as $dl){?>     	    <?php if($dObjLingue->nLang>1):$classSub=' class="sub_box" '?>     		<div id="sub_header_<?php echo  $dl->a?>" class="utilityTools">     			<a href="#" onclick='$("#sub_box_<?php echo  $dl->a?>_<?php echo  $isModal?>").toggle(); return false'  class="acco_box">     			<h3 class="title_<?php echo  $dl->a?>"><?php echo  $dl->b?></h3></a>     		</div><?php endif ?>     		<div id="sub_box_<?php echo  $dl->a?>_<?php echo  $isModal?>" <?php echo  $classSub?>  style="display:none">     			<table class="tableForma">     				<?php $i=1;     				   $itemNum='';     				   $writeSeo=1;     				   foreach ($objSez->fieldspec as $chiave => $valore){     				   	   		if($valore['lang']==1){     				   	   			$langTab=TABLE_CATEGORYTREE_DESCRIZIONI;     				   	   			$langIdPage=($objSez->Data->Dominio)?$objSez->Data->Dominio:$Id_sez;     				   	   			$langField=$chiave;     				   	   			$langLabel=$valore['label'];     				   	   			$itemNum=(!$itemNum)?1:(int)$dObjLingue->nLang*$i;     				   	   			$langObj= new HTMLLingue();     				   	   			$langObj->cssStyle=$valore['cssClass'];     				   	   			if($mode!='edit')$langObj->setWhere('Disable',0);     				   	   			if($valore['type']=='text')$langObj->TetxAreaSize=' style="width:'.((int)$valore['size']-10).'px;height:'.((int)$valore['h']-50).'px" ';									else $langObj->setFieldType('input');									$langObj->getQuery();									$langObj->extraMsg=$valore['extraMsg'];									echo $langObj->createHTML($langTab,$Id,$langField,$itemNum,$langIdPage,$dl->a,$langLabel);									$i++;								}					   }
	                  foreach ($objSez->fieldspec as $chiave => $valore) {	                  		    	if(SEO_ENABLED==1) {	                  		    		if($valore['seo']==1){	                  		    			$langTab=TABLE_CATEGORYTREE_DESCRIZIONI;	                  		    			if($writeSeo==1){	                  		    				echo $seoTitle="<tr><td colspan=\"2\"><h3 class=\"title_".$dl->a."\">".SEO_TAGS."</h3></td></tr>";												$writeSeo=0;											}											$langIdPage=($objSez->Data->Dominio)?$objSez->Data->Dominio:$Id_sez;											$langField=$chiave;	    														$langLabel=$valore['label'];	    														$itemNum=(!$itemNum)?1:(int)$dObjLingue->nLang*$i;	    														$langObj= new HTMLLingue();	    														$langObj->cssStyle=$valore['cssClass'];	    														$langObj->showCount=$valore['showCount'];	    														if($mode!='edit')$langObj->setWhere('Disable',0);	    														if($valore['type']=='text')$langObj->TetxAreaSize=' style="width:'.((int)$valore['size']-10).'px;height:'.((int)$valore['h']-50).'px" ';											else $langObj->setFieldType('input');											$langObj->getQuery();											$langObj->extraMsg=$valore['extraMsg'];											echo $langObj->createHTML($langTab,$Id,$langField,$itemNum,$langIdPage,$dl->a,$langLabel);										    $i++;                      				}              		 }                    }?>             </table>           </div>    <?php } endif ?></div>